              required
              className="w-full bg-[#2A2A2A] text-white px-4 py-2 rounded-lg"
            />
          </div>
          <div>
            <label className="block mb-1">ÏÉà ÎπÑÎ∞ÄÎ≤àÌò∏</label>
            <input
              type="password"
              name="newPassword"
              value={form.newPassword}
              onChange={handleChange}
              required
              className="w-full bg-[#2A2A2A] text-white px-4 py-2 rounded-lg"
            />
          </div>
          <div>
            <label className="block mb-1">ÏÉà ÎπÑÎ∞ÄÎ≤àÌò∏ ÌôïÏù∏</label>
            <input
