import React from 'react';
import { Link } from 'react-router-dom';

/**
 * Layout 컴포넌트는 전체 페이지에서 공통으로 사용되는 UI 구성요소입니다.
 * 상단 네비게이션 바, 본문 children, 하단 푸터로 구성됩니다.
 */
const Layout = ({ children }) => {
  return (
    <div className="min-h-screen bg-[#121212] text-white font-sans flex flex-col">
      {/* 헤더 (네비게이션 바) */}
      <header className="bg-[#1E1E1E] px-6 py-4 flex justify-between items-center">
        <Link to="/" className="text-xl font-bold text-white">Filmus</Link>
        <nav className="space-x-4">
          <Link to="/recommend" className="hover:underline">추천</Link>
          <Link to="/together" className="hover:underline">같이보기</Link>
          <Link to="/mypage" className="hover:underline">MY</Link>
          <Link to="/login" className="hover:bg-custom px-3 py-1 rounded">로그인</Link>
        </nav>
      </header>

      {/* 본문 영역 */}
      <main className="flex-grow px-4 sm:px-6 lg:px-8 py-10">
        {children}
      </main>

      {/* 푸터 */}
      <footer className="bg-[#1E1E1E] text-center py-4 text-gray-400 text-sm">
        © 2024 Filmus. All rights reserved.
      </footer>
    </div>
  );
};

export default Layout;
