import React, { useState } from 'react';

const TogetherPage = () => {
  const [roomCode, setRoomCode] = useState('');
  const [message, setMessage] = useState('');
  const [chat, setChat] = useState([]);

  const handleJoin = () => {
    if (roomCode.trim()) {
      alert(`Î∞© ÏΩîÎìú ${roomCode}Î°ú ÏûÖÏû•Ìï©ÎãàÎã§.`);
    }
  };

  const sendMessage = () => {
    if (message.trim()) {
      setChat([...chat, { text: message, user: 'ÎÇò' }]);
      setMessage('');
    }
  };

  return (
    <Layout>  {/* Layout Ï†ÅÏö© ÏãúÏûë */}
    <div className="min-h-screen bg-[#121212] text-white font-sans px-6 py-20">
      <div className="max-w-4xl mx-auto space-y-12">
        <h1 className="text-2xl font-bold">üé¨ Í∞ôÏù¥Î≥¥Í∏∞ Î∞© ÏûÖÏû•</h1>

        <div className="bg-[#1E1E1E] p-6 rounded-lg space-y-4">
          <label className="block">Î∞© ÏΩîÎìú ÏûÖÎ†•</label>
          <input
            value={roomCode}
            onChange={(e) => setRoomCode(e.target.value)}
            placeholder="ROOM123"
            className="w-full bg-[#2A2A2A] text-white px-4 py-2 rounded-lg"
          />
          <button onClick={handleJoin} className="bg-custom text-white px-4 py-2 rounded-button">ÏûÖÏû•ÌïòÍ∏∞</button>
        </div>

        <div className="bg-[#1E1E1E] p-6 rounded-lg space-y-4">
          <h2 className="text-xl font-semibold">üí¨ Ï±ÑÌåÖÏ∞Ω</h2>
          <div className="h-60 bg-[#2A2A2A] p-4 rounded-lg overflow-y-auto space-y-2">
            {chat.map((c, idx) => (
              <div key={idx} className="text-sm"><strong>{c.user}:</strong> {c.text}</div>
            ))}
          </div>
          <div className="flex gap-2">
            <input
              value={message}
              onChange={(e) => setMessage(e.target.value)}
              placeholder="Î©îÏãúÏßÄÎ•º ÏûÖÎ†•ÌïòÏÑ∏Ïöî"
              className="flex-grow bg-[#2A2A2A] px-4 py-2 rounded-lg"
            />
            <button onClick={sendMessage} className="bg-custom text-white px-4 py-2 rounded-button">Î≥¥ÎÇ¥Í∏∞</button>
          </div>
        </div>
      </div>
    </div>
    </Layout>
  );
};

export default TogetherPage;
